import osimport speech_recognition as srdef speak(text):    os.system(f"say {text}")def listen():    recognizer = sr.Recognizer()    with sr.Microphone() as source:        print("Listening...")        audio = recognizer.listen(source)        try:            text = recognizer.recognize_google(audio)            print(f"You said: {text}")            return text.lower()        except sr.UnknownValueError:            speak("Sorry, I didn't catch that. Please repeat.")            return ""        except sr.RequestError:            speak("Sorry, there was an issue with the speech service.")            return ""def parse_command(command):    if "hello" in command or "hi" in command:        return "greeting"    elif "how are you" in command:        return "status_check"    elif "bye" in command:        return "farewell"    else:        return "unknown"def handle_command(command):    action = parse_command(command)    if action == "greeting":        speak("Hello! How can I assist you today?")    elif action == "status_check":        speak("I'm doing well, thank you. How can I assist you?")    elif action == "farewell":        speak("Goodbye! Have a great day!")    else:        speak("Sorry, I didn't understand that.")if __name__ == "__main__":    while True:        command = listen()        if command:            handle_command(command)            if "bye" in command:                break